package com.easygbs.easygbd.adapter;import android.content.Context;import android.widget.TextView;import com.easygbs.easygbd.R;import com.easygbs.easygbd.activity.MainActivity;import com.easygbs.easygbd.bean.CameraBean;import com.easygbs.easygbd.viewadapter.CommonAdapter;import com.easygbs.easygbd.viewadapter.ViewHolder;import java.util.List;public class CameraAdapter extends CommonAdapter<CameraBean> {    public String TAG= CameraAdapter.class.getSimpleName();    private Context context;    private MainActivity mMainActivity;    public List<CameraBean> CameraBeanList;    public CameraAdapter(Context context, MainActivity mMainActivity, int layoutId, List<CameraBean> CameraBeanList) {        super(context, layoutId, CameraBeanList);        this.context = context;        this.mMainActivity=mMainActivity;        this.CameraBeanList=CameraBeanList;    }    @Override    protected void convert(ViewHolder holder, CameraBean mCameraBean, int position) {        TextView tvName = holder.getView(R.id.tvname);        String name=mCameraBean.getName();        tvName.setText(name);        int isst=mCameraBean.getIsst();        if(isst==0){            tvName.setTextColor(context.getResources().getColor(R.color.color_4d4d4d));        }else if(isst==1){            tvName.setTextColor(context.getResources().getColor(R.color.color_01c9a7));        }    }}