package com.easygbs.easygbd.adapter;import android.content.Context;import android.widget.TextView;import com.easygbs.easygbd.R;import com.easygbs.easygbd.activity.MainActivity;import com.easygbs.easygbd.bean.LocationfrequencyBean;import com.easygbs.easygbd.viewadapter.CommonAdapter;import com.easygbs.easygbd.viewadapter.ViewHolder;import java.util.List;public class LocationfrequencyAdapter extends CommonAdapter<LocationfrequencyBean> {    public String TAG= LocationfrequencyAdapter.class.getSimpleName();    private Context context;    private MainActivity mMainActivity;    public List<LocationfrequencyBean> LocationfrequencyBeanList;    public LocationfrequencyAdapter(Context context, MainActivity mMainActivity, int layoutId, List<LocationfrequencyBean> LocationfrequencyBeanList){        super(context, layoutId, LocationfrequencyBeanList);        this.context = context;        this.mMainActivity=mMainActivity;        this.LocationfrequencyBeanList=LocationfrequencyBeanList;    }    @Override    protected void convert(ViewHolder holder, LocationfrequencyBean mLocationfrequencyBean, int position) {        TextView tvName = holder.getView(R.id.tvname);        String name=mLocationfrequencyBean.getName();        tvName.setText(name);        int isst=mLocationfrequencyBean.getIsst();        if(isst==0){            tvName.setTextColor(context.getResources().getColor(R.color.color_4d4d4d));        }else if(isst==1){            tvName.setTextColor(context.getResources().getColor(R.color.color_01c9a7));        }    }}