package com.easygbs.easygbd.adapter;import android.content.Context;import android.widget.TextView;import com.easygbs.easygbd.R;import com.easygbs.easygbd.activity.MainActivity;import com.easygbs.easygbd.bean.ResolutionBean;import com.easygbs.easygbd.viewadapter.CommonAdapter;import com.easygbs.easygbd.viewadapter.ViewHolder;import java.util.List;public class ResolutionAdapter extends CommonAdapter<ResolutionBean> {    public String TAG= ResolutionAdapter.class.getSimpleName();    private Context context;    private MainActivity mMainActivity;    public List<ResolutionBean> ResolutionBeanList;    public ResolutionAdapter(Context context, MainActivity mMainActivity, int layoutId, List<ResolutionBean> ResolutionBeanList) {        super(context, layoutId, ResolutionBeanList);        this.context = context;        this.mMainActivity=mMainActivity;        this.ResolutionBeanList=ResolutionBeanList;    }    @Override    protected void convert(ViewHolder holder, ResolutionBean mResolutionBean, int position) {        TextView tvName = holder.getView(R.id.tvname);        String name=mResolutionBean.getName();        tvName.setText(name);        int isst=mResolutionBean.getIsst();        if(isst==0){            tvName.setTextColor(context.getResources().getColor(R.color.color_4d4d4d));        }else if(isst==1){            tvName.setTextColor(context.getResources().getColor(R.color.color_01c9a7));        }    }}