package com.easygbs.easygbd.adapter;import android.content.Context;import android.widget.TextView;import com.easygbs.easygbd.R;import com.easygbs.easygbd.activity.MainActivity;import com.easygbs.easygbd.bean.TranproBean;import com.easygbs.easygbd.viewadapter.CommonAdapter;import com.easygbs.easygbd.viewadapter.ViewHolder;import java.util.List;public class TranproAdapter extends CommonAdapter<TranproBean> {    public String TAG= TranproAdapter.class.getSimpleName();    private Context context;    private MainActivity mSetActivity;    public List<TranproBean> SelectCameraResolutionBeanList;    public TranproAdapter(Context context, MainActivity mSetActivity, int layoutId, List<TranproBean> SelectCameraResolutionBeanList) {        super(context, layoutId, SelectCameraResolutionBeanList);        this.context = context;        this.mSetActivity=mSetActivity;        this.SelectCameraResolutionBeanList=SelectCameraResolutionBeanList;    }    @Override    protected void convert(ViewHolder holder, TranproBean mTranproBean, int position) {        TextView tvName = holder.getView(R.id.tvname);        String name=mTranproBean.getName();        tvName.setText(name);        int isst=mTranproBean.getIsst();        if(isst==0){            tvName.setTextColor(context.getResources().getColor(R.color.color_4d4d4d));        }else if(isst==1){            tvName.setTextColor(context.getResources().getColor(R.color.color_01c9a7));        }    }}