package com.easygbs.easygbd.adapter;import android.content.Context;import android.widget.TextView;import com.easygbs.easygbd.R;import com.easygbs.easygbd.activity.MainActivity;import com.easygbs.easygbd.bean.VideoCodeBean;import com.easygbs.easygbd.viewadapter.CommonAdapter;import com.easygbs.easygbd.viewadapter.ViewHolder;import java.util.List;public class VideoCodeAdapter extends CommonAdapter<VideoCodeBean> {    public String TAG= VideoCodeAdapter.class.getSimpleName();    private Context context;    private MainActivity mMainActivity;    public List<VideoCodeBean> VideoCodeBeanList;    public VideoCodeAdapter(Context context, MainActivity mMainActivity, int layoutId, List<VideoCodeBean> VideoCodeBeanList) {        super(context, layoutId, VideoCodeBeanList);        this.context = context;        this.mMainActivity=mMainActivity;        this.VideoCodeBeanList=VideoCodeBeanList;    }    @Override    protected void convert(ViewHolder holder, VideoCodeBean mVideoCodeBean, int position) {        TextView tvName = holder.getView(R.id.tvname);        String name=mVideoCodeBean.getName();        tvName.setText(name);        int isst=mVideoCodeBean.getIsst();        if(isst==0){            tvName.setTextColor(context.getResources().getColor(R.color.color_4d4d4d));        }else if(isst==1){            tvName.setTextColor(context.getResources().getColor(R.color.color_01c9a7));        }    }}